module.exports = async function() {
  const contentUrl = data['content-url-input'] || '';
  const languagesStr = data['target-languages-input'] || '';

  if (!contentUrl || !languagesStr) throw new Error('URL and languages required');

  // Extract content ID from URL
  const urlParts = contentUrl.split('/');
  const lastPart = urlParts[urlParts.length - 1];
  const contentId = lastPart.split('-').pop();
  if (!contentId || contentId.length < 10) throw new Error('Invalid content ID');

  // Parse target languages
  const languages = languagesStr.split(',').map(l => l.trim().toLowerCase()).filter(l => l);
  if (languages.length === 0) throw new Error('No valid languages');

  console.log(`Content ID: ${contentId}`);
  console.log(`Target languages: ${languages.join(', ')}`);

  // Download original HTML from S3
  console.log('Checking existing translations...');
  const versionsResp = await api.frevanaApi.request('GET', `/s3/content/${contentId}/language-versions`);

  let existingLanguages = [];
  if (versionsResp.ok && versionsResp.data?.versions) {
    existingLanguages = versionsResp.data.versions.map(v => v.language_code);
    console.log(`Existing S3 translations: ${existingLanguages.join(', ') || 'none'}`);
  }

  // Check local database for existing translations
  const localTranslations = await api.db.listTranslations({ original_content_id: contentId });
  const localLanguages = localTranslations.map(t => t.language_code);
  console.log(`Existing local translations: ${localLanguages.join(', ') || 'none'}`);

  const languagesToTranslate = languages.filter(lang => !localLanguages.includes(lang) && !existingLanguages.includes(lang));
  console.log(`Languages to translate: ${languagesToTranslate.join(', ') || 'none'}`);

  let originalHtml = '';
  if (languagesToTranslate.length > 0) {
    const firstLang = languagesToTranslate[0];
    const prepareResp = await api.frevanaApi.request('POST', `/s3/content/${contentId}/prepare-translate`, { target_language: firstLang });
    if (!prepareResp.ok) throw new Error('Failed to prepare translation');

    const downloadUrl = prepareResp.data.download_url;
    const response = await api.fetch(downloadUrl);
    originalHtml = await response.text();
    console.log(`Downloaded ${originalHtml.length} characters`);
  }

  return JSON.stringify({ contentId, languages, originalHtml });
};

module.exports = async function() {
  const setupData = JSON.parse(data['setup'] || '{}');
  const languages = setupData.languages || [];
  if (languages.length === 0) throw new Error('No languages to translate');
  console.log(`Extracted ${languages.length} languages: ${languages.join(', ')}`);
  return languages.join('\n');
};

subNodes.script:
module.exports = async function() {
  const setupData = JSON.parse(data['setup'] || '{}');
  const lang = data['translate-loop-loop'] || '';
  const contentId = setupData.contentId;
  const originalHtml = setupData.originalHtml;

  if (!contentId) throw new Error('Missing content ID');
  console.log(`[${lang}] Checking translation status...`);

  // Check local database first
  const existingTranslation = await api.db.getByContentAndLanguage(contentId, lang);
  if (existingTranslation) {
    console.log(`[${lang}] Found in local database (ID: ${existingTranslation.id})`);
    return JSON.stringify({ skip: true, lang, status: 'local_existing', id: existingTranslation.id });
  }

  // Check S3
  const versionsResp = await api.frevanaApi.request('GET', `/s3/content/${contentId}/language-versions`);
  if (versionsResp.ok && versionsResp.data?.versions) {
    const existing = versionsResp.data.versions.find(v => v.language_code === lang);
    if (existing) {
      console.log(`[${lang}] Found in S3`);
      return JSON.stringify({ skip: true, lang, status: 's3_existing', id: existing.content_id, url: existing.url });
    }
  }

  // Need to translate
  console.log(`[${lang}] Need translation`);
  return JSON.stringify({ skip: false, lang, html: originalHtml });
};

module.exports = async function() {
  const prep = JSON.parse(data['check-prepare'] || '{}');
  const translatedHtml = data['translate-html'] || '';
  const lang = prep.lang;
  const setupData = JSON.parse(data['setup'] || '{}');
  const contentId = setupData.contentId;

  if (prep.skip) {
    console.log(`[${lang}] Skipped - ${prep.status}`);
    return JSON.stringify({ lang, status: prep.status, skipped: true, id: prep.id });
  }

  // Extract title from translated HTML
  const titleMatch = translatedHtml.match(/<title[^>]*>([^<]+)<\/title>/i);
  const title = titleMatch?.[1]?.trim() || `Translated to ${lang}`;

  // Save to local database
  const translation = await api.db.createTranslation({
    original_content_id: contentId,
    language_code: lang,
    translated_html: translatedHtml,
    title,
    status: 'draft'
  });

  console.log(`[${lang}] Saved to database (ID: ${translation.id})`);
  return JSON.stringify({ lang, status: 'translated', id: translation.id, title });
};

workflows.script:
module.exports = async function() {
  const results = data['translate-loop'] || '';
  if (!results) return 'No translations completed';

  const items = results.split('\n').filter(l => l.trim()).map(l => JSON.parse(l));
  const setupData = JSON.parse(data['setup'] || '{}');
  const contentId = setupData.contentId;

  const translatedCount = items.filter(i => i.status === 'translated').length;
  const skippedCount = items.filter(i => i.skipped).length;

  let md = '# Translation Completed\n\n';
  md += `**Content ID:** ${contentId}\n\n`;
  md += `**Total:** ${items.length} | **Translated:** ${translatedCount} | **Skipped:** ${skippedCount}\n\n';
  md += '## Translation Summary\n\n';
  md += '| Language | Status | Title | ID |\n';
  md += '|----------|--------|-------|-----|\n';
  items.forEach(i => {
    md += `| ${i.lang.toUpperCase()} | ${i.status} | ${i.title || 'N/A'} | ${i.id || 'N/A'} |\n';
  });
  md += '\n\n**All translations are saved locally.**\n\n';
  md += 'You can:\n';
  md += '- Preview each translation\n';
  md += '- Edit before publishing\n';
  md += '- Publish to S3 when ready\n';

  return md;
};
