module.exports = async function() {
  const contentId = data.userInputs?.contentId || '';
  const languages = data.userInputs?.languages || 'ja,ko,zh-CN,de,fr,es,pt,it,ru,ar';
  const langList = languages.split(',').map(l => l.trim()).filter(Boolean);
  return JSON.stringify({ contentId, languages: langList });
};

module.exports = async function() {
  const results = data['translate-loop'] || '';
  if (!results) return 'No translations completed';

  let items = [];
  const normalizeItem = (entry) => {
    if (!entry) return null;
    if (typeof entry === 'string') {
      try {
        return JSON.parse(entry);
      } catch (e) {
        return { raw: entry };
      }
    }
    if (typeof entry === 'object') return entry;
    return { raw: String(entry) };
  };

  try {
    const parsed = JSON.parse(results);
    if (Array.isArray(parsed)) {
      items = parsed.map(normalizeItem).filter(Boolean);
    } else if (parsed && typeof parsed === 'object') {
      items = [normalizeItem(parsed)].filter(Boolean);
    } else {
      throw new Error('Unexpected loop result format');
    }
  } catch (e) {
    try {
      const lines = results.split('\n').filter(l => l.trim());
      items = lines.map(l => JSON.parse(l));
    } catch (innerError) {
      return `Failed to parse workflow results: ${innerError.message || innerError}`;
    }
  }

  const setupData = JSON.parse(data['setup'] || '{}');
  const contentId = setupData.contentId || 'N/A';

  const extractTitleFromHtml = (html) => {
    if (!html) return '';
    const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
    if (titleMatch?.[1]) return titleMatch[1].trim();
    const h1Match = html.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i);
    if (h1Match?.[1]) {
      return h1Match[1].replace(/<[^>]*>/g, '').trim();
    }
    return '';
  };

  const getUrlFromVersion = (version) => {
    if (!version) return '';
    return (
      version.url ||
      version.download_url ||
      version.content_url ||
      version.preview_url ||
      ''
    );
  };

  const s3UrlMap = new Map();
  const s3TitleMap = new Map();

  if (contentId !== 'N/A') {
    try {
      const versionsResp = await api.frevanaApi.request(
        'GET',
        `/s3/content/${contentId}/language-versions`
      );
      if (versionsResp.ok && versionsResp.data?.versions) {
        versionsResp.data.versions.forEach((version) => {
          const languageCode = version.language_code
            ? String(version.language_code).toLowerCase()
            : '';
          if (!languageCode) return;
          const resolvedUrl = getUrlFromVersion(version);
          if (resolvedUrl) {
            s3UrlMap.set(languageCode, resolvedUrl);
          }
          if (version.title) {
            s3TitleMap.set(languageCode, version.title);
          }
        });
      }
    } catch (error) {
      console.log(`Failed to load S3 versions: ${error?.message || error}`);
    }
  }

  const enrichItems = async (list) => {
    const enriched = [];
    for (const item of list) {
      const rawLang = item.lang || item.language_code || item.language || '';
      const langCode = rawLang ? String(rawLang).toLowerCase() : '';
      let title = item.title;
      let resolvedId = item.id;
      let resolvedUrl = item.url;

      if (langCode && !resolvedUrl && s3UrlMap.has(langCode)) {
        resolvedUrl = s3UrlMap.get(langCode);
      }

      if ((!title || title === 'N/A') && langCode && s3TitleMap.has(langCode)) {
        title = s3TitleMap.get(langCode);
      }

      if ((!title || title === 'N/A') && contentId !== 'N/A' && langCode) {
        const localTranslation = await api.db.getByContentAndLanguage(contentId, langCode);
        if (localTranslation) {
          title = localTranslation.title || title;
          resolvedId = resolvedId || localTranslation.id;
        }
      }

      if ((!title || title === 'N/A') && resolvedUrl) {
        try {
          const response = await api.fetch(resolvedUrl);
          const htmlText = await response.text();
          const extractedTitle = extractTitleFromHtml(htmlText);
          if (extractedTitle) {
            title = extractedTitle;
          }
        } catch (error) {
          console.log(`Failed to fetch title for ${rawLang}: ${error?.message || error}`);
        }
      }

      enriched.push({ ...item, title, id: resolvedId, url: resolvedUrl });
    }
    return enriched;
  };

  items = await enrichItems(items);

  const translatedCount = items.filter(i => i.status === 'translated').length;
  const skippedCount = items.filter(i => i.skipped).length;

  const buildPreviewLink = (item) => {
    const idValue = item.id;
    const numericId = Number(idValue);
    if (Number.isFinite(numericId)) {
      return `[Preview](frevana-translation-preview://?id=${encodeURIComponent(numericId)})`;
    }
    if (item.url) {
      return `[Preview](frevana-translation-preview://?url=${encodeURIComponent(item.url)})`;
    }
    return 'N/A';
  };

  const getStatusLabel = (item) => {
    if (item.status === 'translated') return 'Translated';
    if (item.status === 'local_existing') return 'Already translated (local)';
    if (item.status === 's3_existing') return 'Already translated (cloud)';
    if (item.skipped) return 'Skipped';
    return item.status || 'Unknown';
  };

  let md = '# Translation Completed\n\n';
  md += `**Content ID:** ${contentId}\n\n`;
  md += `**Total:** ${items.length} | **Translated:** ${translatedCount} | **Skipped:** ${skippedCount}\n\n`;
  md += '## Translation Summary\n\n';
  md += '| Language | Status | Title | Preview |\n';
  md += '|----------|--------|-------|---------|\n';
  items.forEach(i => {
    const rawLang = i.lang || i.language_code || i.language || '';
    const lang = rawLang ? String(rawLang).toUpperCase() : 'N/A';
    const title = i.title || 'N/A';
    md += `| ${lang} | ${getStatusLabel(i)} | ${title} | ${buildPreviewLink(i)} |\n`;
  });
  md += '\n\nTranslations are ready for review.\n\n';
  md += 'Next steps:\n';
  md += '- Preview each translation\n';
  md += '- Edit if needed\n';
  md += '- Publish when ready\n';

  return md;
};
