module.exports = async function() {
  const contentUrl = data['content-url-input'] || '';
  const languagesStr = data['target-languages-input'] || '';

  if (!contentUrl || !languagesStr) throw new Error('URL and languages required');

  // Extract content ID from URL
  const urlParts = contentUrl.split('/');
  const lastPart = urlParts[urlParts.length - 1];
  const contentId = lastPart.split('-').pop();
  if (!contentId || contentId.length < 10) throw new Error('Invalid content ID');

  // Parse target languages
  const languages = languagesStr.split(',').map(l => l.trim().toLowerCase()).filter(l => l);
  if (languages.length === 0) throw new Error('No valid languages');

  console.log(`Content ID: ${contentId}`);
  console.log(`Target languages: ${languages.join(', ')}`);

  // Download original HTML from S3
  console.log('Checking existing translations...');
  const versionsResp = await api.frevanaApi.request('GET', `/s3/content/${contentId}/language-versions`);

  let existingLanguages = [];
  if (versionsResp.ok && versionsResp.data?.versions) {
    existingLanguages = versionsResp.data.versions.map(v => v.language_code);
    console.log(`Existing S3 translations: ${existingLanguages.join(', ') || 'none'}`);
  }

  // Check local database for existing translations
  const localTranslations = await api.db.listTranslations({ original_content_id: contentId });
  const localLanguages = localTranslations.map(t => t.language_code);
  console.log(`Existing local translations: ${localLanguages.join(', ') || 'none'}`);

  const languagesToTranslate = languages.filter(lang => !localLanguages.includes(lang) && !existingLanguages.includes(lang));
  console.log(`Languages to translate: ${languagesToTranslate.join(', ') || 'none'}`);

  let originalHtml = '';
  if (languagesToTranslate.length > 0) {
    const firstLang = languagesToTranslate[0];
    const prepareResp = await api.frevanaApi.request('POST', `/s3/content/${contentId}/prepare-translate`, { target_language: firstLang });
    if (!prepareResp.ok) throw new Error('Failed to prepare translation');

    const downloadUrl = prepareResp.data.download_url;
    const response = await api.fetch(downloadUrl);
    originalHtml = await response.text();
    console.log(`Downloaded ${originalHtml.length} characters`);
  }

  return JSON.stringify({ contentId, languages, originalHtml });
};

