module.exports = async function() {
  const setupData = JSON.parse(data['setup'] || '{}');
  const lang = data['translate-loop-loop'] || '';
  const contentId = setupData.contentId;
  const originalHtml = setupData.originalHtml;

  if (!contentId) throw new Error('Missing content ID');
  console.log(`[${lang}] Checking translation status...`);

  // Check local database first
  const existingTranslation = await api.db.getByContentAndLanguage(contentId, lang);
  if (existingTranslation) {
    console.log(`[${lang}] Found in local database (ID: ${existingTranslation.id})`);
    return JSON.stringify({ skip: true, lang, status: 'local_existing', id: existingTranslation.id });
  }

  // Check S3
  const versionsResp = await api.frevanaApi.request('GET', `/s3/content/${contentId}/language-versions`);
  if (versionsResp.ok && versionsResp.data?.versions) {
    const existing = versionsResp.data.versions.find(v => v.language_code === lang);
    if (existing) {
      console.log(`[${lang}] Found in S3`);
      return JSON.stringify({ skip: true, lang, status: 's3_existing', id: existing.content_id, url: existing.url });
    }
  }

  // Need to translate
  console.log(`[${lang}] Need translation`);
  return JSON.stringify({ skip: false, lang, html: originalHtml });
};

module.exports = async function() {
  const prep = JSON.parse(data['check-prepare'] || '{}');
  const translatedHtml = data['translate-html'] || '';
  const lang = prep.lang;
  const setupData = JSON.parse(data['setup'] || '{}');
  const contentId = setupData.contentId;

  if (!contentId) {
    throw new Error('Missing content ID in save-translation step');
  }

  if (prep.skip) {
    console.log(`[${lang}] Skipped - ${prep.status}`);
    return JSON.stringify({ lang, status: prep.status, skipped: true, id: prep.id });
  }

  // Extract title from translated HTML
  const titleMatch = translatedHtml.match(/<title[^>]*>([^<]+)<\/title>/i);
  const title = titleMatch?.[1]?.trim() || `Translated to ${lang}`;

  // Save to local database
  const translation = await api.db.createTranslation({
    original_content_id: contentId,
    language_code: lang,
    translated_html: translatedHtml,
    title,
    status: 'draft'
  });

  console.log(`[${lang}] Saved to database (ID: ${translation.id})`);
  return JSON.stringify({ lang, status: 'translated', id: translation.id, title });
};

